import { useState } from "react";
import { useRef } from "react";
import { useEffect } from "react";

const SimpleInput = (props) => {

  const [enteredName, setEnteredName] = useState('')
  const [enteredNameTouched, setEnteredNameTouched] = useState(false)

  const enteredNameIsValid = enteredName.trim !== '';
  const enteredNameIsInvalid = !enteredNameIsValid && enteredNameTouched

  const changeInputHandler = event => {
    setEnteredName(event.target.value)
  }

  const nameInputBlurHandler = () => {
    setEnteredNameTouched(true)
  }

  const formSubmissionHandler = event => {
    event.preventDefault();
    if (enteredNameIsInvalid) {
      return;
    }

    console.log('Form Submittion:', enteredName)
    setEnteredName('')

  }
  const nameInputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control';
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={changeInputHandler} onBlur={nameInputBlurHandler} value={enteredName}/>
        {nameInputIsInvalid && <p className="error-text">Entered Name isn't valid.</p>}
      </div>
      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
