import { useState } from "react";
import { useEffect } from "react";
import useInput from "../hooks/use-input";

const SimpleInput = (props) => {
  const regex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

  const [enteredEmail, setEnteredEmail] = useState("");
  const [emailInputIsTouched, setEmailInputIsTouched] = useState(false);
  const [formIsValid, setFormIsValid] = useState(false);

  const enteredEmailIsInvalid = enteredEmail.match(regex) == null;
  const enteredEmailIsValid = !(enteredEmailIsInvalid && emailInputIsTouched);

  console.log("email: ", enteredEmailIsValid);
  console.log("name: ", enteredNameIsValid);
  console.log("form: ", formIsValid);

  const {
    value: enteredName,
    hasError: nameInputHasError,
    InputChangeHandler: nameChangeHandler,
    inputBlurHandler: nameBlurHandler,
    isValid: enteredNameIsValid

  } = useInput(value => value.trim().length == 0 )

  useEffect(() => {
    if (enteredNameIsValid && enteredEmailIsValid) {
      setFormIsValid(true);
    } else {
      setFormIsValid(false);
    }
  }, [enteredEmail, enteredName]);

  const nameInputChangeHandler = (event) => {
    setNameInputIsTouched(true);
    setEnteredName(event.target.value);
  };

  const emailInputChangeHandler = (event) => {
    setEmailInputIsTouched(true);
    setEnteredEmail(event.target.value);
  };

  const emailBlurHandler = () => {
    setEmailInputIsTouched(true);
  };

  const nameInputBlurHandler = () => {
    setNameInputIsTouched(true);
  };

  const submitFormHandler = (event) => {
    event.preventDefault();
    setNameInputIsTouched(true);
    setEmailInputIsTouched(true);

    if (formIsValid) {
      console.log(enteredName);
      console.log(enteredEmail);
      console.log(formIsValid);
      setEnteredName("");
      setEnteredEmail("");
      setNameInputIsTouched(false);
      setEmailInputIsTouched(false);
      setFormIsValid(false);
    } else {
      return;
    }
  };

  const className = enteredNameIsValid
    ? "form-control"
    : "form-control invalid";
  return (
    <form className={className} onSubmit={submitFormHandler}>
      <div>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          placeholder="Input Name"
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
        {!enteredNameIsValid && (
          <p className="error-text">Entered Name isn't valid.</p>
        )}
      </div>
      <div>
        <label htmlFor="name">Your Email</label>
        <input
          type="text"
          id="name"
          placeholder="Input Email"
          onChange={emailInputChangeHandler}
          onBlur={emailBlurHandler}
          value={enteredEmail}
        />
        {!enteredEmailIsValid && (
          <p className="error-text">Entered Email isn't valid.</p>
        )}
      </div>
      <div className="form-actions">
        <button type="submit" disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
