import { useState } from "react";
import { useEffect } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('')
  const [nameInputIsTouched, setNameInputIsTouched] = useState(false)
  const enteredNameIsValid = (enteredName.trim().length !== 0) && (nameInputIsTouched)


  const nameInputChangeHandler = event => {
    setNameInputIsTouched(true)
    setEnteredName(event.target.value)
    console.log(enteredName)
    console.log(enteredNameIsValid)
  }

  const className = enteredNameIsValid ? "form-control" : "form-control invalid"
  return (
    <form className={className}>
      <div>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" placeholder="Input Name" onChange={nameInputChangeHandler}/>
        {/* <p className="error-text">Entered Name isn't valid.</p> */}
      </div>
      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
