import { useState } from "react";
import { useRef } from "react";

const SimpleInput = (props) => {

  const [enteredName, setEnteredName] = useState('')
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false)
  const [enteredNameIsTouched, setEnteredNameIsTouched] = useState(false)

  useEffect(() => {
    if(enteredNameIsValid) {
      console.log('username is valid')
    }
  },[enteredNameIsValid])

  const changeInputHandler = event => {
    setEnteredName(event.target.value)
    console.log('EnteredName:', enteredName)
  }



  const formSubmissionHandler = event => {
    event.preventDefault();
    if (enteredName.trim() === '') {
      setEnteredNameIsValid(false)
      return;
    }
    setEnteredNameIsValid(true)
    setEnteredNameIsTouched(true)
    console.log('Form Submittion:', enteredName)
    setEnteredName('')
  }
  const enteredNameIsInvalid = !enteredNameIsValid && enteredNameIsTouched
  const nameInputClasses = enteredNameIsInvalid ? 'form-control invalid' : 'form-control';


  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={changeInputHandler} value={enteredName}/>
        {enteredNameIsInValid && <p className="error-text">Entered Name isn't valid.</p>}
      </div>
      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
