import { useState } from "react";
import { useEffect } from "react";

const SimpleInput = (props) => {
  const regex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/

  const [enteredName, setEnteredName] = useState('')
  const [enteredEmail, setEnteredEmail] = useState('');
  const [nameInputIsTouched, setNameInputIsTouched] = useState(false)
  const [emailInputIsTouched, setEmailInputIsTouched] = useState(false)
  const [formIsValid, setFormIsInvalid] = useState(false)

  const enteredNameIsInValid = enteredName.trim().length == 0
  const enteredEmailIsInvalid = enteredEmail.match(MAIL_FORMAT)
  const enteredNameIsValid = !((enteredNameIsInValid) && (nameInputIsTouched))

  console.log(enteredEmailIsInvalid)

  useEffect(() => {

    },[enteredNameIsValid])



  const nameInputChangeHandler = event => {
    setNameInputIsTouched(true)
    setEnteredName(event.target.value)
  }

  const emailInputChangeHandler = event => {
    setEmailInputIsTouched(true)
    setEnteredEmail(event.target.value)
  }

  const emailBlurHandler = () => {
    setEmailInputIsTouched(true)
  }

  const nameBlurHandler = () => {
    setNameInputIsTouched(true)
  }

  const submitFormHandler = () => {
    nameInputIsTouched(true)

    if (!enteredNameIsInValid) {
      return;
    }
    console.log(enteredName)
    setEnteredName('')
    setNameInputIsTouched(false)
  }



  const className = (enteredNameIsValid ? "form-control" : "form-control invalid")
  return (
    <form className={className} onSubmit={submitFormHandler}>
      <div>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" placeholder="Input Name" onChange={nameInputChangeHandler} onBlur={nameBlurHandler}/>
        {!enteredNameIsValid && <p className="error-text">Entered Name isn't valid.</p>}
      </div>
      <div>
        <label htmlFor="name">Your Email</label>
        <input type="text" id="name" placeholder="Input Email" onChange={emailInputChangeHandler} onBlur={emailBlurHandler}/>
        {!enteredNameIsValid && <p className="error-text">Entered Email isn't valid.</p>}
      </div>
      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
