import { useState } from "react";
import { useRef } from "react";

const SimpleInput = (props) => {

  const [enteredName, setEnteredName] = useState('')
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false)
  const nameInputRef = useRef('');

  const changeInputHandler = event => {
    setEnteredName(event.target.value)
    console.log('EnteredName:', enteredName)
  }



  const formSubmissionHandler = event => {
    event.preventDefault();
    if (enteredName.trim() === '') {
      setEnteredNameIsValid(false)
      return;
    }
    setEnteredNameIsValid(true)
    console.log('Form Submittion:', enteredName)
    console.log('Form Submittion:', nameInputRef.current.value)

  }
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className='form-control'>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={changeInputHandler} ref={nameInputRef}/>
        {!enteredNameIsValid && <p>Entered Name isn't valid.</p>}
      </div>
      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
